const props = [ 'black', 'red', 'green', 'yellow', 'blue', 'magenta', 'cyan', 'white', 'gray', 'grey', 'brightRed', 'brightGreen', 'brightYellow', 'brightBlue', 'brightMagenta', 'brightCyan', 'brightWhite', 'bgBlack', 'bgRed', 'bgGreen', 'bgYellow', 'bgBlue', 'bgMagenta', 'bgCyan', 'bgWhite', 'bgGray', 'bgGrey', 'bgBrightRed', 'bgBrightGreen', 'bgBrightYellow', 'bgBrightBlue', 'bgBrightMagenta', 'bgBrightCyan', 'bgBrightWhite', 'reset', 'bold', 'dim', 'italic', 'underline', 'inverse', 'hidden', 'strikethrough', 'rainbow', 'zebra', 'america', 'trap', 'random' ];


function extend(target) {
	Object.defineProperties(
		target,
		Object.fromEntries(
			props.map(
				prop => [
					prop,
					{
						get: function () {
							/* extend(this) */
							return this
						}
					}
				]
			)
		)
	)
	return target
}

extend(String.prototype);



